mapper.select:
SELECT ord.id, ord.order_number AS orderNumber, ord.customer_id, ord.transaction_number AS transactionNumber, ord.payment_time AS paymentTime, ord.goods_id, ord.bond, ord.bond_channel AS bondChannel, ord.total_number AS totalNumber, ord.total_number_all AS totalNumberAll, ord.reference_commission_all AS referenceCommissionAll, ord.reference_commission AS referenceCommission, ord.preferential_amount AS preferentialAmount, ord.actual_payment AS actualPayment, ord.tail_amount AS tailAmount, ord.tail_amount_channel AS tailAmountChannel, ord.logistics_info AS logisticsInfo, ord.status, ord.remark, ord.address_id AS addressId, ord.create_time AS createTime, ord.resell_size AS resellSize, ag.goods_price AS goodsPrice, ag.goods_name AS goodsName, org.name, img.url AS imgUrl FROM goods_order ord INNER JOIN auction_goods ag ON ord.goods_id = ag.id INNER JOIN customer cu ON cu.id = ord.customer_id INNER JOIN organizer org ON org.id = ag.organizer_id left JOIN (select * from images i where i.type=1 GROUP BY i.subject_id) img ON img.subject_id = ord.goods_id WHERE ord.customer_id = #{customerId}
AND ord.status =#{status}
order by ord.id desc
